    <body>
        <h1>默认主题---登录内嵌页  (无布局)</h1>
        <div id="main"></div>

        <script type="text/javascript" src="http://res.yilewan.com/etpResource/js/jquery-1.9.1.min.js"></script>
        <script type="text/javascript" src="http://pk-res.yilewan.com/home/js/sdk/yilewanClientGameSDK.1.0.1.min.js"></script>
        <script type="text/javascript">

            window.clientSDK.init({
                el:'#main',
                gameId:'land',
                defaultMainPage: 'login',
                defaultArticle: {'news':5},
                defaultSection: {'mini_slider':5},
                checkFomCallback: function(mes,obj){
                    if(mes){
                        $('.error-txt').html(mes).css("visibility", "visible");
                    }else{
                        $('.error-txt').html('').css("visibility", "hidden");
                    }
                },
                initEnd: function(data,SDK){
                    console.log(data);

                    SDK.$el.on("loging", function(){
                       $('.error-txt').html('正在登录中...').css("visibility", "visible");
                   });

                    SDK.$el.on("loginError", function(event,res){
                       $('.error-txt').html(res.info).css("visibility", "visible");
                   });

                    SDK.$el.on("loginSuccess", function(event,res){
                        $('.error-txt').html("").css("visibility", "hidden");
                        console.log(JSON.stringify(res.data));
						SCBrowserCallMono(JSON.stringify(res.data));

                   });

                    SDK.$el.on("reging", function(){
                        $('.error-txt').html('正在注册中...').css("visibility", "visible");
                    });

                    SDK.$el.on("regError", function(event,res){
                        $('.error-txt').html(res.info).css("visibility", "visible");
                    });

                    SDK.$el.on("regSuccess", function(event,res){
                        $('.error-txt').html('注册成功').css("visibility", "visible");
                    });

                    SDK.$el.on("switchToRegister", function(event,target){
                        console.log('切换到注册')
                    });

                    SDK.$el.on("switchToLogin", function(event,target){
                        console.log('切换到登录')
                    });
                }
            });
        </script>
    </body>
